<script>
    //vars for line animation
    let path= document.querySelector('.path')
    console.log(path)
    let pathLength= path.getTotalLength();

    path.style.strokeDasharray = pathLength + ' ' + pathLength
    path.style.strokeDashoffset = pathLength

    
    window.addEventListener ('scroll', ()=>{
        //animating the visibility of info sections
        let title = document.getElementById('title')
        let fadeInPoints= [['project1', [['i1','.15'] , ['i2','.27'], ['i3','.07'], ['i4','.23']]], ['project2', [['i1','.65'] , ['i2','.47'], ['i3','.57'], ['i4','.42']]], ['project3', [['i1','.70'] , ['i2','.80'], ['i3','.98'], ['i4','.92']]] ]
            let scrollTop = window.scrollY;
            console.log(scrollTop)
            let docHeight = document.body.offsetHeight;
            let winHeight = window.innerHeight;
        
        let scrollPercent = scrollTop / (docHeight - winHeight);
         
        //animate line
        let drawLength = (pathLength * scrollPercent);
        
        path.style.strokeDashoffset= pathLength-drawLength

        fadeInPoints.forEach(setVis)
        function setVis(project, i){
            for(let idx = 0; idx <= 3; idx ++){
                let el = document.getElementById(`${project[0]}-${project[1][idx][0]}`);
                if(project[1][idx][1] < scrollPercent){
                   el.style.opacity = '1'
                }
                else{
                    el.style.opacity = '0'
                }
            }
        }

        //change title
        if (scrollPercent >0.01){
            title.style.color = 'transparent'
        }
        else{
            title.style.color= "white"
        }   
    })
    
    
    
</script>

